(652, 299, '_wp_attachment_metadata', 'a:5:{s:5:"width";i:298;s:6:"height";i:448;s:4:"file";s:14:"2013/07/60.jpg";s:5:"sizes";a:2:{s:9:"thumbnail";a:4:{s:4:"file";s:14:"60-150x150.jpg";s:5:"width";i:150;s:6:"height";i:150;s:9:"mime-type";s:10:"image/jpeg";}s:6:"medium";a:4:{s:4:"file";s:14:"60-199x300.jpg";s:5:"width";i:199;s:6:"height";i:300;s:9:"mime-type";s:10:"image/jpeg";}}s:10:"image_meta";a:10:{s:8:"aperture";d:7.0999999999999996;s:6:"credit";s:13:"DAVIDMAN DROR";s:6:"camera";s:10:"NIKON D300";s:7:"caption";s:0:"";s:17:"created_timestamp";i:1284496036;s:9:"copyright";s:0:"";s:12:"focal_length";s:3:"125";s:3:"iso";s:3:"200";s:13:"shutter_speed";s:5:"0.005";s:5:"title";s:0:"";}}'),
