(1598, 39, 'wpcf-resume_field', '1963-present Patent Attorney and Advocate, Tel Aviv, Israel\r\n\r\n1954-1963 Patent Attorney, Burroughs Corporation, Detroit, Michigan, USA\r\n\r\n1952-1954 Patent Attorney, Judge Advocates Office, Wright Air Development Center, Wright-Patterson Air Force Base, Dayton, Ohio, USA\r\n\r\n1947-1952 Examiner in the U.S. Patent &amp; Trademark Office, Washington, D.C., USA'),
(1599, 39, 'wpcf-experience_field', 'Mr. Barish is engaged primarily in the preparation and prosecution of Patent Applications in Israel, USA, and most countries throughout the world.\r\n\r\nFor most of this period, operated own firm.\r\n\r\nAt the present time, Of Counsel to Ehrlich &amp; Fenster.\r\n\r\n&nbsp;'),
(1600, 39, 'wpcf-education_field', '1946 B.Sc. degree in Electronic Engineering from the Illinois Institute of Technology, Chicago, Illinois, USA\r\n\r\n1950 J.D. from the George Washington University, Washington, D.C., USA\r\n\r\n1951 LL.M. from the George Washington University, Washington, D.C., USA'),
(1601, 39, 'wpcf-admissions', 'Mr. Barish is admitted to practice in the Bars of Washington, D.C., Michigan and Israel.\r\n\r\nMr. Barish is registered to practice before the U.S. Patent &amp; Trademark Office and the Patent Office in Israel.'),
