tor.', '', 'inherit', 'closed', 'closed', '', '258-revision-12', '', '', '2013-07-07 13:35:26', '2013-07-07 13:35:26', '', 258, 'http://ipatent.co.il/?p=339', 0, 'revision', '', 0),
